package String;

public class CompareTo {//this will compare two strings based on their character(ASCI values)

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1="sachin";
		String s2="SACHIN";
		String s3="Ratan";
		
		System.out.println(s1.compareTo(s3));
		System.out.println(s1.compareTo(s2));

	}

}
