package corejava;

public class DataTypesDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int value1=9/2;
		float value2=101f/61f;
		double value3=10d/6d;
		float value4=5/2.f;//float division
		System.out.println("value1is"+value1);
		System.out.println("value2is"+value2);
		System.out.println("value3is"+value3);
		System.out.println("value4is"+value4);
		

	}

}
